# I. feladatimport randomfogalmak = [    'majorság', 'hűbéres', 'jobbágy', 'nemes', 'tized',    'kilenced', 'robot', 'szügyhám', 'vetésforgó', 'ugar', 'lovag']meghatarozasok = [    'Egy-egy nagybirtok vagy valamely részének igazgatási központja.',    'Aki örökletes használatra megkapja a földet.',    'Telkes paraszt, aki a földesúrtól kapott földön gazdálkodik.',    'Kiváltságos réteg.',    'Egyházi adó.',    'Földesúrnak beszolgáltatott adó.',    'Ötvenkét igás, vagy 104 kézimunka nap kötelezettség.',    'Igavonási találmány, melynek köszönhetően nem az állat nyakában van a húzó eszköz.',    'A termőföld használata évszakonként más és más.',    'Művelés alá nem vont terület.',    'Vagyonos katonai szolgálattevő lóval, páncéllal.']def create_dictioary(fogalmak, meghatarozasok):    """ létrehozza a fogalmak és meghatározások dict.-ét """    return {meghatarozas: fogalom for fogalom, meghatarozas in zip(fogalmak, meghatarozasok)}def get_random_deifiniton(fogalom_meghatarozas_dict):    """ véletlenszerűen választ egy meghatározást """    meghatarozas = random.choice(list(fogalom_meghatarozas_dict.keys()))    return meghatarozas, fogalom_meghatarozas_dict[meghatarozas]def check_answer(user_answer, correct_answer):    """ ellenőrízzük az eredményt """    return user_answer.lower().strip() == correct_answer.lower()def ask_question(meghatarozas, correct_fogalom):    """ feltett kérdés kezelése """    print(f'(END\Q\QUIT) Meghatározás: {meghatarozas}')    valasz = input("Melyik fogalom társul ehhez?: ").strip()    return valasz, correct_fogalomdef display_result(correct_answers, total_questions):    """ kiírja az eredményt """    print(f'Helyes válaszok száma: {correct_answers} / {total_questions}')    if total_questions > 0:        print(f'Sikerességi arány: {correct_answers / total_questions * 100:.2f}%')def play_game(fogalom_meghatarozas_dict):    """ játék főprogramja """    correct_answers = 0    total_questions = 0    fogalom_meghatarozas_keys_in_list = list(fogalom_meghatarozas_dict.keys())    random.shuffle(fogalom_meghatarozas_keys_in_list)    for meghatarozas in fogalom_meghatarozas_keys_in_list:        correct_fogalom = fogalom_meghatarozas_dict[meghatarozas]        valasz, correct_fogalom = ask_question(meghatarozas, correct_fogalom)        if valasz.lower() in ["end", "q", "quit"]:            break        total_questions += 1        if check_answer(valasz, correct_fogalom):            print("Helyes válasz!")            correct_answers += 1        else:            print(f'Rossz válasz, helyesen: {correct_fogalom}')    return correct_fogalom, total_questionsdef main():    fogalom_meghatarozas_dict = create_dictioary(fogalmak, meghatarozasok)    correct_answers, total_questions = play_game(fogalom_meghatarozas_dict)    display_result(correct_answers, total_questions)main()